//\u5728\u5173\u95ed\u5c4f\u5e55\u65f6\u4f7f\u7528\uff0c\u56e0\u6b64\u6211\u4eec\u53ef\u4ee5\u8fd4\u56de\u5c06\u5176\u6253\u5f00\u7684\u6309\u94ae\u3002\n    private GameObject m_PreviouslySelected;\n\n    //\u6211\u4eec\u5728\u68c0\u67e5\u65f6\u9700\u8981\u6bd4\u5bf9\u7684\u52a8\u753b\u5668\u72b6\u6001\u548c\u8fc7\u6e21\u540d\u79f0\u3002\n    const string k_OpenTransitionName ="Open";\n    const string k_ClosedStateName = "Closed";\n\n    public void OnEnable()\n    {\n        //\u6211\u4eec\u5c06\u54c8\u5e0c\u7f13\u5b58\u5230 "Open" \u53c2\u6570\uff0c\u56e0\u6b64\u53ef\u63d0\u4f9b\u7ed9 Animator.SetBool\u3002\n        m_OpenParameterId = Animator.StringToHash (k_OpenTransitionName);\n\n        //\u73b0\u5728\u6253\u5f00\u521d\u59cb\u5c4f\u5e55\uff08\u5982\u679c\u5df2\u8bbe\u7f6e\uff09\u3002\n        if (initiallyOpen == null)\n            return;\n        OpenPanel(initiallyOpen);\n    }\n\n    //\u5173\u95ed\u5f53\u524d\u6253\u5f00\u7684\u9762\u677f\u5e76\u6253\u5f00\u63d0\u4f9b\u7684\u9762\u677f\u3002\n    //\u8fd8\u8d1f\u8d23\u5904\u7406\u5bfc\u822a\uff0c\u8bbe\u7f6e\u65b0\u7684\u9009\u5b9a\u5143\u7d20\u3002\n    public void OpenPanel (Animator anim)\n    {\n        if (m_Open == anim)\n            return;\n\n        //\u6fc0\u6d3b\u65b0\u7684\u5c4f\u5e55\u5c42\u7ea7\u89c6\u56fe\uff0c\u4ee5\u4fbf\u5bf9\u5176\u8fdb\u884c\u52a8\u753b\u5316\u3002\n        anim.gameObject.SetActive(true);\n        //\u4fdd\u5b58\u5f53\u524d\u9009\u5b9a\u7684\u7528\u4e8e\u6253\u5f00\u6b64\u5c4f\u5e55\u7684\u6309\u94ae\u3002\uff08CloseCurrent \u4f1a\u5bf9\u5176\u8fdb\u884c\u4fee\u6539\uff09\n        var newPreviouslySelected = EventSystem.current.currentSelectedGameObject;\n        //\u5c06\u5c4f\u5e55\u79fb\u5230\u524d\u9762\u3002\n        anim.transform.SetAsLastSibling();\n\n        CloseCurrent();\n\n        m_PreviouslySelected = newPreviouslySelected;\n\n        //\u5c06\u65b0\u5c4f\u5e55\u8bbe\u7f6e\u4e3a\u6253\u5f00\u7684\u5c4f\u5e55\u3002\n        m_Open = anim;\n        //\u542f\u52a8\u6253\u5f00\u52a8\u753b\n        m_Open.SetBool(m_OpenParameterId, true);\n\n        //\u5c06\u65b0\u5c4f\u5e55\u4e2d\u7684\u4e00\u4e2a\u5143\u7d20\u8bbe\u7f6e\u4e3a\u65b0\u7684\u9009\u5b9a\u5143\u7d20\u3002\n        GameObject go = FindFirstEnabledSelectable(anim.gameObject);\n        SetSelected(go);\n    }\n\n    //\u67e5\u627e\u63d0\u4f9b\u7684\u5c42\u7ea7\u89c6\u56fe\u4e2d\u7684\u7b2c\u4e00\u4e2a\u53ef\u9009\u5143\u7d20\u3002\n    static GameObject FindFirstEnabledSelectable (GameObject gameObject)\n    {\n        GameObject go = null;\n        var selectables = gameObject.GetComponentsInChildren&lt;Selectable&gt; (true);\n        foreach (var selectable in selectables) {\n            if (selectable.IsActive () && selectable.IsInteractable ()) {\n                go = selectable.gameObject;\n                break;\n            }\n        }\n        return go;\n    }\n\n    //\u5173\u95ed\u5f53\u524d\u6253\u5f00\u7684\u5c4f\u5e55\n    //\u8fd8\u8d1f\u8d23\u5904\u7406\u5bfc\u822a\u3002\n    //\u5c06\u9009\u5b9a\u5143\u7d20\u8fd8\u539f\u4e3a\u6253\u5f00\u5f53\u524d\u5c4f\u5e55\u4e4b\u524d\u4f7f\u7528\u7684\u53ef\u9009\u5143\u7d20\u3002\n    public void CloseCurrent()\n    {\n        if (m_Open == null)\n            return;\n\n        //\u542f\u52a8\u5173\u95ed\u52a8\u753b\u3002\n        m_Open.SetBool(m_OpenParameterId, false);\n\n        //\u5c06\u9009\u5b9a\u5143\u7d20\u8fd8\u539f\u4e3a\u6253\u5f00\u5f53\u524d\u5c4f\u5e55\u4e4b\u524d\u4f7f\u7528\u7684\u53ef\u9009\u5143\u7d20\u3002\n        SetSelected(m_PreviouslySelected);\n        //\u5173\u95ed\u52a8\u753b\u7ed3\u675f\u65f6\u542f\u52a8\u534f\u7a0b\u4ee5\u7981\u7528\u8be5\u5c42\u7ea7\u89c6\u56fe\u3002\n        StartCoroutine(DisablePanelDeleyed(m_Open));\n        //\u65e0\u6253\u5f00\u5c4f\u5e55\u3002\n        m_Open = null;\n    }\n\n    //\u534f\u7a0b\u5c06\u68c0\u6d4b\u5173\u95ed\u52a8\u753b\u4f55\u65f6\u7ed3\u675f\uff0c\u5e76\u4f1a\u505c\u7528\n    //\u5c42\u7ea7\u89c6\u56fe\u3002\n    IEnumerator DisablePanelDeleyed(Animator anim)\n    {\n        bool closedStateReached = false;\n        bool wantToClose = true;\n        while (!closedStateReached && wantToClose)\n        {\n            if (!anim.IsInTransition(0))\n                closedStateReached = anim.GetCurrentAnimatorStateInfo(0).IsName(k_ClosedStateName);\n\n            wantToClose = !anim.GetBool(m_OpenParameterId);\n\n            yield return new WaitForEndOfFrame();\n        }\n\n        if (wantToClose)\n            anim.gameObject.SetActive(false);\n    }\n\n    //\u9009\u5b9a\u63d0\u4f9b\u7684\u6e38\u620f\u5bf9\u8c61\n    //\u5f53\u4f7f\u7528\u9f20\u6807/\u89e6\u6478\u65f6\uff0c\u6211\u4eec\u5b9e\u9645\u4e0a\u60f3\u8981\u5c06\u5176\u8bbe\u7f6e\u4e3a\u5148\u524d\u9009\u5b9a\u7684\u5bf9\u8c61\u5e76\u4e14\n    //\u73b0\u5728\u4e0d\u5c06\u4efb\u4f55\u5bf9\u8c61\u8bbe\u7f6e\u4e3a\u9009\u5b9a\u72b6\u6001\u3002\n    private void SetSelected(GameObject go)\n    {\n        //\u9009\u62e9\u6e38\u620f\u5bf9\u8c61\u3002\n        EventSystem.current.SetSelectedGameObject(go);\n\n        //\u5982\u679c\u6211\u4eec\u73b0\u5728\u6b63\u5728\u4f7f\u7528\u952e\u76d8\uff0c\u90a3\u4e48\u6211\u4eec\u4fbf\u5b8c\u6210\u4e86\u6240\u6709\u8bbe\u7f6e\u3002\n        var standaloneInputModule = EventSystem.current.currentInputModule as StandaloneInputModule;\n        if (standaloneInputModule != null)\n            return;\n\n        //\u7531\u4e8e\u6211\u4eec\u4f7f\u7528\u7684\u662f\u6307\u9488\u8bbe\u5907\uff0c\u56e0\u6b64\u6211\u4eec\u4e0d\u5e0c\u671b\u9009\u62e9\u4efb\u4f55\u5185\u5bb9\u3002\n        //\u4f46\u662f\u5982\u679c\u7528\u6237\u5207\u6362\u5230\u952e\u76d8\uff0c\u6211\u4eec\u5e0c\u671b\u4ece\u63d0\u4f9b\u7684\u6e38\u620f\u5bf9\u8c61\u5f00\u59cb\u5bfc\u822a\u3002\n        //\u6240\u4ee5\uff0c\u6b64\u5904\u6211\u4eec\u5c06\u5f53\u524d\u7684\u9009\u5b9a\u9879\u8bbe\u7f6e\u4e3a null\uff0c\u56e0\u6b64\u63d0\u4f9b\u7684\u6e38\u620f\u5bf9\u8c61\u6210\u4e3a EventSystem \u4e2d\u7684\u6700\u540e\u9009\u5b9a\u9879\u3002\n        EventSystem.current.SetSelectedGameObject(null);\n    }\n}\n')),(0,r.kt)("p",null,"\u8ba9\u6211\u4eec\u8fde\u63a5\u6b64\u811a\u672c\uff0c\u4e3a\u6b64\u9700\u8981\u521b\u5efa\u65b0\u7684\u6e38\u620f\u5bf9\u8c61\uff0c\u6211\u4eec\u53ef\u4ee5\u5c06\u5176\u91cd\u547d\u540d\u4e3a\u201cScreenManager\u201d\u4e4b\u7c7b\u7684\u540d\u79f0\uff0c\u5e76\u5c06\u4e0a\u9762\u7684\u7ec4\u4ef6\u6dfb\u52a0\u5230\u8be5\u6e38\u620f\u5bf9\u8c61\u3002\u60a8\u53ef\u4ee5\u4e3a\u5176\u6307\u5b9a\u521d\u59cb\u5c4f\u5e55\uff0c\u6b64\u5c4f\u5e55\u5c06\u5728\u573a\u666f\u542f\u52a8\u65f6\u6253\u5f00\u3002"),(0,r.kt)("p",null,"\u73b0\u5728\uff0c\u6700\u540e\u7684\u5de5\u4f5c\u662f\u8ba9 ",(0,r.kt)("a",o({parentName:"p"},{href:"/docs/uitoolkits/com-unity-ugui/uireference/comp-uiinteraction/script-button"}),"UI \u6309\u94ae"),"\u751f\u6548\u3002\u9009\u62e9\u5e94\u89e6\u53d1\u5c4f\u5e55\u8fc7\u6e21\u7684\u6309\u94ae\uff0c\u5e76\u5728 Inspector \u7684  ",(0,r.kt)("strong",{parentName:"p"},"On Click ()"),"  \u5217\u8868\u4e0b\u6dfb\u52a0\u4e00\u4e2a\u65b0\u64cd\u4f5c\u3002\u5c06\u521a\u521b\u5efa\u7684 ScreenManager \u6e38\u620f\u5bf9\u8c61\u62d6\u5230 ObjectField\uff0c\u5728\u4e0b\u62c9\u9009\u5355\u4e2d\u9009\u62e9  ",(0,r.kt)("strong",{parentName:"p"},"ScreenManager ",">"," OpenPanel (Animator)")," \uff0c\u7136\u540e\u5c06\u7528\u6237\u70b9\u51fb\u6309\u94ae\u65f6\u8981\u6253\u5f00\u7684\u9762\u677f\u62d6\u653e\u5230 ObjectField\u3002"),(0,r.kt)("p",null,(0,r.kt)("img",o({parentName:"p"},{src:"https://docs.unity3d.com/cn/2022.1/uploads/Main/UI_ScreenTransitionButtonInspector.png",alt:"Button Inspector"}))),(0,r.kt)("p",null,"Button Inspector"),(0,r.kt)("h2",o({},{id:"\u6ce8\u610f"}),"\u6ce8\u610f"),(0,r.kt)("p",null,"\u8fd9\u79cd\u65b9\u6cd5\u53ea\u8981\u6c42\u6bcf\u4e2a\u5c4f\u5e55\u6709\u4e00\u4e2a\u52a8\u753b\u63a7\u5236\u5668 (AnimatorController) \u4ee5\u53ca\u4e00\u4e2a Open \u53c2\u6570\u548c\u4e00\u4e2a Closed \u72b6\u6001\u5373\u53ef\u53d1\u6325\u4f5c\u7528\uff08\u65e0\u8bba